// Merge Inputs - Entry point normalization
// Detects mode from input source and sets execution context

const inputData = $input.first().json;

// Webhook wraps payload in 'body', Schedule Trigger doesn't
const payload = inputData.body || inputData;

let executionContext;

if (payload.mode === 'refabrication') {
  // From Webhook - refabrication mode
  executionContext = {
    mode: 'refabrication',
    fat_asset_id: payload.asset_id,
    thin_asset_id: payload.asset_id.replace(/^fat:/, 'thin:'),
    new_version: payload.new_version || 'v2.0',
    previous_version: payload.previous_version || 'v1.0',
    refabrication_reason: payload.refabrication_reason || 'change_detected',
    changed_fields: payload.changed_fields || [],
    source_queue_entry_id: payload.source_queue_entry_id || null,
    requires_fresh_scrape: payload.requires_fresh_scrape || false
  };
} else {
  // From Schedule Trigger - normal polling mode
  executionContext = {
    mode: 'normal',
    fat_asset_id: null,
    thin_asset_id: null,
    new_version: 'v1.0',
    previous_version: null,
    refabrication_reason: null,
    changed_fields: [],
    source_queue_entry_id: null,
    requires_fresh_scrape: false
  };
}

return [{ json: executionContext }];

// Prepare Fetch ThinObject - Refabrication Mode
// FIXED: Now includes direct fields from OBJECT-1 v3.x

const executionContext = $input.first().json;

return [{
  json: {
    execution_context: executionContext,
    neo4j_payload: {
      statement: `
        MATCH (thin:ThinObject {asset_id: $thin_asset_id})
        WHERE thin.object_type = $object_type
        RETURN {
          object_id: thin.asset_id,
          name: thin.name,
          orcid: thin.orcid,
          email: thin.email,
          institution: thin.institution,

          // NEW: Direct fields from OBJECT-1 v3.x
          biography: thin.biography,
          research_interests: thin.research_interests,
          current_projects: thin.current_projects,
          expertise_keywords: thin.expertise_keywords,
          collaborators: thin.collaborators,
          keywords: thin.keywords,
          publications: thin.publications,
          patents: thin.patents,
          affiliations: thin.affiliations,
          grants: thin.grants,
          metrics: thin.metrics,
          data_quality_flags: thin.data_quality_flags,
          data_completeness: thin.data_completeness,

          // Legacy JSON fields (backward compatibility)
          orcid_data: thin.orcid_data,
          scopus_data: thin.scopus_data,
          scholar_data: thin.scholar_data,
          university_data: thin.university_data,
          patents_data: thin.patents_data,
          source_metadata: thin.source_metadata,
          created_at: thin.created_at
        } as thin_object
        LIMIT 1
      `,
      parameters: {
        thin_asset_id: executionContext.thin_asset_id,
        object_type: "researcher"
      }
    }
  }
}];

// Parse Neo4j Response - Refabrication Mode
// Parses response for specific ThinObject, sets refab status

const response = $input.first().json;
const executionContext = $('Prepare Fetch ThinObject (Refab)').first().json.execution_context;

// Handle empty results or errors
if (!response.data || !response.data.values || response.data.values.length === 0) {
  return [{
    json: {
      execution_context: executionContext,
      neo4j_payload: null,
      thin_objects: [],
      skipped: true,
      message: `ThinObject not found: ${executionContext.thin_asset_id}`
    }
  }];
}

// Parse Neo4j response
const thinObjects = response.data.values.map(row => row[0]);
const objectIds = thinObjects.map(obj => obj.object_id);

return [{
  json: {
    execution_context: executionContext,
    neo4j_payload: {
      statement: `
        UNWIND $object_ids AS object_id
        MATCH (thin:ThinObject {asset_id: object_id})
        SET thin.status = $new_status
        SET thin.refabrication_started_at = timestamp()
        SET thin.refabrication_version = $new_version
        RETURN thin.asset_id as updated_id
      `,
      parameters: {
        object_ids: objectIds,
        new_status: "refabricating",
        new_version: executionContext.new_version
      }
    },
    thin_objects: thinObjects
  }
}];

// Split ThinObjects - Normal Mode
// Gets thin_objects and execution_context from Prepare node

const prepareData = $('Prepare Set Status Fabricating').first().json;
const thinObjects = prepareData.thin_objects;
const executionContext = prepareData.execution_context;

// Attach execution context to each thin object
return thinObjects.map(obj => ({ 
  json: {
    ...obj,
    execution_context: executionContext
  }
}));

// Split ThinObjects - Refab Mode
// Gets thin_objects and execution_context from Prepare node

const prepareData = $('Prepare Set Status (Refab)').first().json;
const thinObjects = prepareData.thin_objects;
const executionContext = prepareData.execution_context;

// Attach execution context to each thin object
return thinObjects.map(obj => ({ 
  json: {
    ...obj,
    execution_context: executionContext
  }
}));

// Check Execution Mode for Response
// Routes to appropriate completion handling

const executionContext = $('Prepare PostgreSQL Payload').first().json.execution_context || {};
const isRefabrication = executionContext.mode === 'refabrication';

return [{
  json: {
    is_refabrication: isRefabrication,
    execution_context: executionContext
  }
}];

