// Pass 1: Structural Unification - FIXED VERSION
// Merges data from multiple sources and creates unified researcher profile
// FIX: Uses pre-merged thin.publications (with year/citations) instead of source-specific data
// MODIFIED: Preserves execution_context through pipeline

const thinObject = $input.first().json;
const executionContext = thinObject.execution_context;

function titleSimilarity(title1, title2) {
  const words1 = new Set(title1.toLowerCase().split(/\s+/));
  const words2 = new Set(title2.toLowerCase().split(/\s+/));

  if (words1.size === 0 || words2.size === 0) return 0.0;

  const intersection = new Set([...words1].filter(x => words2.has(x)));
  const union = new Set([...words1, ...words2]);

  return intersection.size / union.size;
}

// Parse JSON fields safely
function safeParseJSON(str, fallback = {}) {
  if (!str) return fallback;
  if (typeof str === 'object') return str;
  try {
    return JSON.parse(str);
  } catch (e) {
    return fallback;
  }
}

// Parse source-specific data (for fallback and additional fields)
const orcidData = safeParseJSON(thinObject.orcid_data, {});
const scopusData = safeParseJSON(thinObject.scopus_data, {});
const scholarData = safeParseJSON(thinObject.scholar_data, {});
const universityData = safeParseJSON(thinObject.university_data, {});
const patentsData = safeParseJSON(thinObject.patents_data, {});
const sourceMetadata = safeParseJSON(thinObject.source_metadata, {
  sources_scraped: [],
  scrape_timestamps: {},
  field_hashes: {}
});

// ============================================================
// FIX: Parse pre-merged publications from ThinObject FIRST
// These come from OBJECT-1's Merge node and have complete data
// ============================================================
let preMergedPublications = [];
try {
  const pubData = thinObject.publications;
  if (typeof pubData === 'string') {
    preMergedPublications = JSON.parse(pubData);
  } else if (Array.isArray(pubData)) {
    preMergedPublications = pubData;
  }
} catch (e) {
  console.log('Could not parse thin.publications, will use source-specific data');
}

// Also get pre-merged collaborators, keywords, affiliations from ThinObject
let preMergedCollaborators = [];
try {
  const collabData = thinObject.collaborators;
  if (typeof collabData === 'string') {
    preMergedCollaborators = JSON.parse(collabData);
  } else if (Array.isArray(collabData)) {
    preMergedCollaborators = collabData;
  }
} catch (e) {}

let preMergedKeywords = [];
try {
  const kwData = thinObject.keywords;
  if (typeof kwData === 'string') {
    preMergedKeywords = JSON.parse(kwData);
  } else if (Array.isArray(kwData)) {
    preMergedKeywords = kwData;
  }
} catch (e) {}

let preMergedAffiliations = [];
try {
  const affData = thinObject.affiliations;
  if (typeof affData === 'string') {
    preMergedAffiliations = JSON.parse(affData);
  } else if (Array.isArray(affData)) {
    preMergedAffiliations = affData;
  }
} catch (e) {}

let preMergedMetrics = {};
try {
  const metData = thinObject.metrics;
  if (typeof metData === 'string') {
    preMergedMetrics = JSON.parse(metData);
  } else if (typeof metData === 'object' && metData !== null) {
    preMergedMetrics = metData;
  }
} catch (e) {}

// Initialize unified object
const unified = {
  object_id: thinObject.object_id || thinObject.asset_id,
  name: thinObject.name,
  orcid: thinObject.orcid || null,
  email: thinObject.email || null,
  institution: thinObject.institution || null,
  publications: [],
  collaborators: [],
  keywords: [],
  affiliations: [],
  patents: [],
  grants: [],
  biography: thinObject.biography || universityData.biography || '',
  research_interests: thinObject.research_interests || universityData.research_interests || [],
  current_projects: thinObject.current_projects || universityData.current_projects || [],
  expertise_keywords: thinObject.expertise_keywords || universityData.expertise_keywords || [],
  metrics: {},
  data_quality_flags: [],
  source_metadata: sourceMetadata,
  execution_context: executionContext
};

// ============================================================
// PUBLICATIONS: Use pre-merged data if available (HAS YEAR/CITATIONS)
// ============================================================
if (preMergedPublications.length > 0) {
  console.log(`Using ${preMergedPublications.length} pre-merged publications from ThinObject`);
  unified.publications = preMergedPublications.map(pub => ({
    title: pub.title,
    doi: pub.doi || null,
    year: pub.year || null,
    citations: pub.citations || 0,
    authors: pub.authors || [],
    abstract: pub.abstract || '',
    keywords: pub.keywords || [],
    publication_venue: pub.publication_venue || pub.publication || '',
    source: pub.source || 'merged'
  }));
} else {
  // FALLBACK: Merge from source-specific data (old behavior)
  console.log('No pre-merged publications, falling back to source-specific merge');
  const publicationsMap = new Map();

  // Add Scopus publications
  (scopusData.publications || []).forEach(pub => {
